var pi = "";





function setup() {
  c1 = createCanvas(1800, 1800);
  background(0);
  noLoop();
}


function draw() {

  var radius = width / 1.08;
  beginShape();
  fill(0);
  stroke(255);
  ellipse(width / 2, height / 2, radius);
  endShape();

  var x = [];
  var y = [];
  var j;
  j = 0;
  translate(width / 2, height / 2);
  for (var i = 0; i <= 2 * PI + 2 * PI / 10; i += 2 * PI / 10) {
    noStroke();
    x[j] = (radius / 2) * cos(i);
    y[j] = (radius / 2) * sin(i);
    ellipse(x[j], y[j], 1);
    j++;

  }

  for (var k = 1; k <= 10; k++) {
    textSize(30);
    noStroke();
    fill(255);
    if (k - 1 > 0 && k - 1 < 5) {
      text(k - 1, x[k - 1] - 10, y[k - 1] + 25);
    } else if (k - 1 == 5) {
      text(k - 1, x[k - 1] - 20, y[k - 1]);

    } else if (k - 1 > 5 && k - 1 < 9) {
      text(k - 1, x[k - 1] - 15, y[k - 1] - 15);

    } else {
      text(k - 1, x[k - 1], y[k - 1]);
    }
  }

  for (i = 0; i < pi.length; i++) {
    var d1 = parseInt(pi[i]);
    var d2 = parseInt(pi[i + 1]);
    noFill();



    if (d1 != d2) {
      colorMode(RGB);
      if (d1 == 0) {
        stroke(255, 0, 0);
      } else if (d1 == 1) {
        stroke(0, 0, 255);
      } else if (d1 == 2) {
        stroke(0, 255, 0);
      } else if (d1 == 3) {
        stroke(255, 247, 0);
      } else if (d1 == 4) {
        stroke(0, 247, 255);
      } else if (d1 == 5) {
        stroke(255, 0, 247);
      } else if (d1 == 6) {
        stroke(255, 60, 0);
      } else if (d1 == 7) {
        stroke(255, 255, 255);
      } else if (d1 == 8) {
        stroke(128, 0, 128);
      } else if (d1 == 9) {
        stroke(0, 128, 128);
      }



      //stroke(d1*randomGaussian(0,28),d1*randomGaussian(0,28),d1*randomGaussian(0,28),5);

      beginShape();
      vertex(x[d1], y[d1]);
      bezierVertex(x[d1], y[d1], randomGaussian(-20, 20), randomGaussian(-20, 20), x[d2], y[d2]);
      endShape();

    }
  }

  //save("pi.png");
}
